import './index.css'
const TodoItem = props => {
  const {itemDetails, deleteItems} = props
  const {id, title} = itemDetails
  const onDelete = () => {
    deleteItems(id)
  }
  return (
    <li className="list">
      <div className="alignment">
        <p className="para">{title}</p>
        <button className="button" onClick={onDelete}>
          delete
        </button>
      </div>
    </li>
  )
}
export default TodoItem
